&lt;img data-src=&#34;https://cdn.nlark.com/yuque/0/2024/png/40371717/1711628328876-e3fb815a-22a1-4b32-8571-0d53084ea16f.png#averageHue=%23faf7d4&amp;amp;clientId=u579708c9-233f-4&amp;amp;from=paste&amp;amp;height=709&amp;amp;id=u0c55555d&amp;amp;originHeight=886&amp;amp;originWidth=1104&amp;amp;originalType=binary&amp;amp;ratio=1.25&amp;amp;rotation=0&amp;amp;showTitle=false&amp;amp;size=90634&amp;amp;status=done&amp;amp;style=none&amp;amp;taskId=u5d646344-7789-4c53-a6d1-5c4856e3b1a&amp;amp;title=&amp;amp;width=883.2&#34; alt=&#34;image.png&#34;&gt;&lt;br&gt;
 利用脚本得到 flag&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;30&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;31&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;32&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;33&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;34&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;import base64&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def get_base64_diff_value(s1, s2):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    base64chars = &amp;#x27;ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/&amp;#x27;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    res = 0&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for i in range(len(s2)):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if s1[i] != s2[i]:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return abs(base64chars.index(s1[i]) - base64chars.index(s2[i]))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return res&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def solve_stego():&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    with open(&amp;#x27;output328.txt&amp;#x27;, &amp;#x27;r&amp;#x27;) as f:  # 修改为&amp;#x27;r&amp;#x27;模式&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        file_lines = f.readlines()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        bin_str = &amp;#x27;&amp;#x27;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for line in file_lines:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            steg_line = line.strip()  # 使用strip()移除换行符&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            norm_line = base64.b64encode(base64.b64decode(steg_line)).decode(&amp;#x27;ascii&amp;#x27;).strip()  # 使用base64模块进行编解码，并转换为字符串&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            diff = get_base64_diff_value(steg_line, norm_line)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(diff)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            pads_num = steg_line.count(&amp;#x27;=&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            if diff:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                bin_str += bin(diff)[2:].zfill(pads_num * 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                bin_str += &amp;#x27;0&amp;#x27; * pads_num * 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(goflag(bin_str))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def goflag(bin_str):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    res_str = &amp;#x27;&amp;#x27;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for i in range(0, len(bin_str), 8):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        res_str += chr(int(bin_str[i:i + 8], 2))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return res_str&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;if __name__ == &amp;#x27;__main__&amp;#x27;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    solve_stego()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p fazhazhenhaoting=&#34;&#34;&gt;&lt;img data-src=&#34;https://cdn.nlark.com/yuque/0/2024/png/40371717/1711629192682-1905f5aa-60ba-45d1-96ae-acdae0e37182.png#averageHue=%23fcfbfa&amp;amp;clientId=u579708c9-233f-4&amp;amp;from=paste&amp;amp;height=182&amp;amp;id=uac4a6ed6&amp;amp;originHeight=227&amp;amp;originWidth=373&amp;amp;originalType=binary&amp;amp;ratio=1.25&amp;amp;rotation=0&amp;amp;showTitle=false&amp;amp;size=14192&amp;amp;status=done&amp;amp;style=none&amp;amp;taskId=uc2a0c4c1-600e-4822-936f-fd9a110b1b1&amp;amp;title=&amp;amp;width=298.4&#34; alt=&#34;image.png&#34;&gt;&lt;br&gt;
